import React, { useEffect, useRef, useState } from 'react';
import { Button } from '@/shared/ui/button';
import { renderAsync } from 'docx-preview';
import { Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious } from '@/shared/ui/carousel';
import { Card } from '@/shared/ui/card';

const WordFileProcessorWithToc: React.FC = () => {
   const [error, setError] = useState<string | null>(null);
   const [toc, setToc] = useState<{ title: string; id: string; href?: string }[]>([]);
   const viewerRef = useRef<HTMLDivElement | null>(null);

   // Загружаем файл из localStorage при загрузке страницы
   useEffect(() => {
      const savedFile = localStorage.getItem('wordFile');

      if (savedFile) {
         loadFile(savedFile);
         console.log(savedFile);
      } else {
         loadFile(
            'data:application/octet-stream;base64,',
         );
      }
   }, []);

   const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
      const file = event.target.files?.[0];
      if (file) {
         setError(null);
         setToc([]);
         const arrayBuffer = await file.arrayBuffer();
         try {
            if (viewerRef.current) {
               viewerRef.current.innerHTML = '';
            }

            const base64File = await arrayBufferToBase64(arrayBuffer);
            localStorage.setItem('wordFile', base64File); // Сохраняем файл в localStorage

            await renderAsync(arrayBuffer, viewerRef.current!, undefined, {
               inWrapper: true,
            });

            extractToc();
         } catch (error) {
            console.error('Ошибка при рендеринге документа:', error);
            setError('Не удалось обработать документ.');
         }
      }
   };

   // Преобразование ArrayBuffer в Base64
   const arrayBufferToBase64 = (buffer: ArrayBuffer): Promise<string> => {
      return new Promise((resolve) => {
         const blob = new Blob([buffer]);
         const reader = new FileReader();
         reader.onloadend = () => {
            resolve(reader.result as string);
         };
         reader.readAsDataURL(blob);
      });
   };

   // Преобразование Base64 в ArrayBuffer
   const base64ToArrayBuffer = (base64: string): Promise<ArrayBuffer> => {
      return new Promise((resolve) => {
         const binaryString = atob(base64.split(',')[1]);
         const len = binaryString.length;
         const bytes = new Uint8Array(len);
         for (let i = 0; i < len; i++) {
            bytes[i] = binaryString.charCodeAt(i);
         }
         resolve(bytes.buffer);
      });
   };

   const loadFile = async (base64: string) => {
      try {
         const arrayBuffer = await base64ToArrayBuffer(base64);
         if (viewerRef.current) {
            viewerRef.current.innerHTML = '';
         }
         await renderAsync(arrayBuffer, viewerRef.current!, undefined, {
            inWrapper: true,
         });
         extractToc();
      } catch (error) {
         console.error('Ошибка при загрузке сохраненного файла:', error);
         setError('Не удалось загрузить сохраненный файл.');
      }
   };

   const extractToc = () => {
      if (!viewerRef.current) return;

      const headings = viewerRef.current.querySelectorAll('.docx_12, .docx_21, .docx_11');
      const tocItems: { title: string; id: string; href?: string }[] = [];

      headings.forEach((heading, index) => {
         const id = `heading-${index}`;
         heading.setAttribute('id', id);
         const link = heading.querySelector('a[href]');
         const href = link?.getAttribute('href') || null;
         tocItems.push({
            title: heading.textContent?.trim() || `Элемент ${index + 1}`,
            id,
            // @ts-ignore
            href,
         });
      });
      setToc(tocItems);
   };

   const handleScrollToElement = (id: string, href?: string) => {
      let targetElement: HTMLElement | null = null;

      if (href) {
         targetElement = viewerRef.current?.querySelector(href) as HTMLElement;
      } else {
         // @ts-ignore
         targetElement = viewerRef.current?.querySelector(`#${id}`);
      }

      if (targetElement && viewerRef.current) {
         const containerTop = viewerRef.current.getBoundingClientRect().top;
         const elementTop = targetElement.getBoundingClientRect().top;

         const scrollOffset = elementTop - containerTop - 30;

         viewerRef.current.scrollTo({
            top: viewerRef.current.scrollTop + scrollOffset,
            behavior: 'smooth',
         });
      }
   };

   return (
      <div>
         {/* Загрузка файла */}
         <Button size='small' className='mb-4 mt-4 flex gap-[10px]'>
            <label htmlFor='upload-file' className='cursor-pointer'>
               <p>Загрузить файл</p>
            </label>
         </Button>
         <input type='file' id='upload-file' accept='.docx' style={{ display: 'none' }} onChange={handleFileUpload} />
         <div className='flex justify-center'>
            {toc.length > 0 && (
               <Carousel className='flex w-[90%] justify-center'>
                  <CarouselContent className='-ml-1 flex items-center'>
                     {toc.map((item) => (
                        <CarouselItem key={item.id} className='pl-1 md:basis-1/2 lg:basis-1/3'>
                           <div className='p-1'>
                              <Card>
                                 <button
                                    key={item.id}
                                    className='text-center hover:underline'
                                    onClick={() => handleScrollToElement(item.id, item.href)}
                                 >
                                    {item.title}
                                 </button>
                              </Card>
                           </div>
                        </CarouselItem>
                     ))}
                  </CarouselContent>
                  <CarouselPrevious />
                  <CarouselNext />
               </Carousel>
            )}
         </div>

         {error && <p className='mt-2 text-red-500'>{error}</p>}

         <div
            ref={viewerRef}
            className='docx-viewer mt-4 rounded border border-gray-300 p-4'
            style={{ maxHeight: '77vh', overflow: 'auto' }}
         />
      </div>
   );
};

export default WordFileProcessorWithToc;
